func SantaFloor(santa string) int{
	var floor int
	for _, ch := range santa {
		if ch == ')'{
			floor--
		} else if ch == '('{
			floor++
		}
	}
	return floor
}
// afunction that determine the position of the bracket that made santa go to the basment of the building
func SantaBasement(santa string) int{
	var floor int
	for i, ch := range santa {
		if ch == ')'{
			floor--
			if floor == -1 {
                return i+1
            }
		} else if ch == '('{
			floor++
		}
	}
	return floor
}
